import { Button } from "./Button";
import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
const Main_Section = () => {
  return (
    <>
      <Wrapper>
        <div className="container">
          <div className="grid grid_two_columns">
            <div className="main_section">
              <p className="para">welcome to</p>
              <h1 className="heading">grocery store</h1>
              <p>
                Buy grocery and more from brands such as Kellogg's, Tata Gold,
                Parle G, Lays, etc. Order the online grocery comfortably from
                home for timely doorstep delivery.
              </p>
              <NavLink>
                <Button>shop now</Button>
              </NavLink>
            </div>
            <div classname="main_section_image">
                <figure>
                <img src="./images/main_section_image.jpg" alt="main_section_image" className="image"/>
                </figure>
            </div>
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
padding:12rem 0;
  .container {
    max-width: 120rem;
    display: grid;
    text-transform: capitalize;

    .grid_two_columns {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      .main_section_image{
          .image{
            width:10rem;
            height:10rem;
          }

      }
    }
  }
`; // 1fr=25% of the available space.
export default Main_Section;
