import React, { useEffect } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { useProductContext } from "./context/productContext";
import PageNavigation from "./components/PageNavigation";
import MyImage from "./components/MyImage";
import FormatPrice from "./components/FormatPrice";
import LocalShippingIcon from "@mui/icons-material/LocalShipping";

const API = "https://api.pujakaitem.com/api/products";

function SingleProduct() {
  const { getSingleProduct, isSingleLoading, singleProduct } =
    useProductContext();

  const { id } = useParams();

  //destructure
  const {
    id: singlep,
    name,
    company,
    price,
    description,
    category,
    stock,
    stars,
    reviews,
    image,
  } = singleProduct;

  useEffect(() => {
    getSingleProduct(`${API}?id=${id}`);
  }, []);

  if (isSingleLoading) {
    return <div>Loading.....</div>;
  }
  return (
    <>
      <Wrapper>
        <PageNavigation title={name} />
        <div className="container">
          <div className="grid grid_two_column">
            <div className="grid-two-rows">
              <div className="product_image">
                <MyImage img={image} />
              </div>
            </div>
            <div className="product_data">
              <h2>{name}</h2>
              <p>{stars}</p>
              <p>{reviews} reviews</p>
              <p className="product_data_price">
                MRP:
                <del>
                  <FormatPrice price={price + 250000} />
                </del>
              </p>
              <p className="product_data_price product_data_real_price">
                Deal of the Day: <FormatPrice price={price} />
              </p>
              <p className="product_description">{description}</p>

              <div className="product_data_warranty">
                <div className="product_data_icon">
                  <LocalShippingIcon />
                  <p>Free Delivery</p>
                </div>

                <div className="product_data_icon">
                  <LocalShippingIcon />
                  <p>Free Delivery</p>
                </div>

                <div className="product_data_icon">
                  <LocalShippingIcon />
                  <p>Free Delivery</p>
                </div>

                <div className="product_data_icon">
                  <LocalShippingIcon />
                  <p>Free Delivery</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Wrapper>
    </>
  );
}
const Wrapper = styled.section`
.product_data_price{
  font-size:1.5rem;
}
.product_data_real_price{
  font-weight:600;
}
.product_description{
  margin-top:1.5rem;
word-spacing:.3rem;
text-align:justify;
font-size:1.6rem;
}
.product_data_warranty{
  display:grid;
  grid-template-columns:repeat(4,1fr);
  gap: 0.5rem;
  margin-top:2rem;
  .product_data_icon{

  }
}
`;

export default SingleProduct;
