import React from "react";
import styled from "styled-components";
import { useCartContext } from "./context/cart_context";
import CartItem from "../components/components/CartItem";
import { Button } from "../components/components/Button";
import { NavLink } from "react-router-dom";
import FormatPrice from "./components/FormatPrice";

const Cart = () => {
  const { cart, clearCart, total_amount, shipping_fee } = useCartContext();
  console.log(cart);

  //Empty page of a cart
  if (cart.length === 0) {
    return (
      <EmptyDiv>
        <h3>No items in cart</h3>
      </EmptyDiv>
    );
  }

  return (
    <Wrapper>
      <div className="container">
        <div className="cart_heading grid-five-column">
          <p>Item</p>
          <p className="cart_hide">Price</p>
          <p>Quantity</p>
          <p>SubTotal</p>
          <p>Remove</p>
        </div>
        <hr />
        <div className="cart_item">
          {cart.map((currentData) => {
            return <CartItem key={currentData.id} {...currentData} />;
          })}
        </div>
        <hr />
        <div className="cart_btn">
          <NavLink to="/products">
            <Button>CONTINUE SHOPPING </Button>
          </NavLink>
          <Button className="btn clear_cart_btn" onClick={clearCart}>
            CLEAR CART
          </Button>
        </div>

        <div className="total_part">
          <div>
            <div>
              <p>SUBTOTAL:</p>
              <p>
                <FormatPrice price={total_amount} />
              </p>
              <div>
                <p>SHIPPING FEE:</p>
                <p>
                  <FormatPrice price={shipping_fee} />
                </p>
              </div>
              <div>
                <p>TOTAL:</p>
                <p>
                  <FormatPrice price={shipping_fee + total_amount} />
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Wrapper>
  );
};
const EmptyDiv = styled.div`
  h3 {
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 16rem 0;
    font-size: 2rem;
  }
`;

const Wrapper = styled.section`
  .container {
    max-width: 90rem 0;
    margin: 10rem 20rem 10rem 20rem;

    .grid-five-column {
      display: grid;
      gap: 16rem;
      grid-template-columns: repeat(5, 1fr);
    }
    .cart_btn {
      display: flex;
      justify-content: space-between;
      align-items: center;

      .clear_cart_btn {
        background: #fa8072;
        position: relative;
      }
    }
    .total_part {
      width: 20rem;
      height: 10rem;
      background: ${({ theme }) => theme.colors.bg};
      padding: 2rem;
      position: absolute;
      bottom: 35%;
      right: 10%;

      p {
        font-size: 1.3rem;
      }
    }
  }
`;
export default Cart;
