import { useState } from "react";
import styled from "styled-components";
import CheckIcon from "@mui/icons-material/Check";
import Count from "./Count";
import { NavLink } from "react-router-dom";
import { Button } from "./Button";
import {useCartContext} from "../context/cart_context";


const AddToCart = ({ product }) => {
  const { id, colors, stock } = product;
  const {addTocart} = useCartContext();

  const [color, setColor] = useState(colors[2]);
  const [count, setCount] = useState(1);

  const INC = () => {
    count < stock ? setCount(count + 1) : setCount(stock);
  };
  const DEC = () => {
    count > 1 ? setCount(count - 1) : setCount(1);
  };


  return (
    <Wrapper>
      <div className="colors">
        <p>
          Colors:
          {colors.map((currentColor, index) => {
            return (
              <button
                key={index}
                style={{ background: currentColor }}
                className={
                  color === currentColor ? "color_btn  active" : "color_btn"
                }
                onClick={() => setColor(currentColor)}
              >
                {color === currentColor ? <CheckIcon className="tick" /> : null}
              </button>
            );
          })}
        </p>
      </div>

      {/* count (increment and decrement) */}
      <Count count={count} inc={INC} dec={DEC} />

      {/* add to cart button */}
     <div className="">
      <NavLink to="/cart" onClick={() =>addTocart(id, color, count , product) }>
        <Button className="count_btn">Add To Cart</Button>
      </NavLink>
     </div>
    </Wrapper>


  );
};
const Wrapper = styled.section`
  .color_btn {
    width: 2rem;
    height: 2rem;
    border: none;
    border-radius: 50%;
    margin-top: 1rem;
    margin-left: 1rem;
    opacity: 0.5;

    &:hover {
      opacity: 1;
    }
  }
  .active {
    opacity: 1;
  }
  .tick {
    color: white;
  }

  Button {
    margin: .2rem 0 0 0;
  }
`;
export default AddToCart;
