import React from "react";
import styled from "styled-components";
import Count from "./Count";
import FormatPrice from "./FormatPrice";
import DeleteIcon from "@mui/icons-material/Delete";
import { useCartContext } from "../context/cart_context";

const CartItem = ({
  id,
  name,
  image,
  price,
  count,
  color,
  stock,
}) => {
  const { deleteItem } = useCartContext();
  const INC = () => {
    // count < stock ? setCount(count + 1) : setCount(stock);
  };
  const DEC = () => {
    // count > 1 ? setCount(count - 1) : setCount(1);
  };
  return (
    <Wrapper>
      <div className="cart_heading grid grid-five-column">
        <div className="cart_data">
          <div className="name_image_color">
            <img className="cart_image" src={image} alt={name} />
            <div className="grid_two_column">
              <p className="name">{name}</p>
              <div className="color_div">
                <p>Color:</p>
                <div
                  className="color_style"
                  style={{ background: color, color: color }}
                ></div>
              </div>
            </div>
          </div>
          <div className="hide-cart">
            <p>
              <FormatPrice price={price} />
            </p>
          </div>

          <div className="count_btn">
            <Count count={count} inc={DEC} dec={INC} />
          </div>

          <div className="hide-cart">
            <p>
              <FormatPrice price={price * count} />
            </p>
          </div>
          <div className="delete_item">
            <DeleteIcon
              className="delete_icon"
              onClick={() => deleteItem(id)}
            />
          </div>
        </div>

      </div>
    </Wrapper>
  );
};
const Wrapper = styled.section`
  max-width: 90rem 0;
  .container {
    margin: 10rem 20rem 10rem 20rem;
  }
  .grid {
    display: grid;
    margin: 2rem 0;
  }

  .grid-five-column {
    display: grid;
    gap: 30rem;
    grid-template-columns: repeat(5, 1fr);

    .cart_data {
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: row;
      gap: 10.3rem;

      .name_image_color {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: row;
        gap: 1.3rem;

        .grid_two_column {
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
          gap: 0.3rem;
        }
      }
      .name {
        display: flex;
        flex-direction: column;
        font-size: 1.5rem;
      }
      img {
        width: 5.8rem;
        height: 4.7rem;
      }
      p {
        font-size: 1.3rem;
      }
      .color_style {
        border: none;
        border-radius: 50%;
        width: 2rem;
        height: 2rem;
      }
      .delete_item {
        .delete_icon {
          font-size: 2.4rem;
        }
      }
    }
  }
`;
export default CartItem;
