import React from "react";
import { useFilterContext } from "../context/Filter_Context";
import { Button } from "./Button";
import styled from "styled-components";
import DoneIcon from '@mui/icons-material/Done';

const FilterSection = () => {
  const {
    filters: { text, category, company, color },
    all_products,
    updateFilterValue,
  } = useFilterContext();

  //to get unique data of all field//
  const getUniqueData = (data, property) => {
    let allCategory = data.map((currentData) => {
      return currentData[property];
    });

    if (property === "colors") {
      // return (allCategory = ["All", ...new Set([].concat(...allCategory))]); //remove dublicate value
        allCategory = allCategory.flat();//remove dublicate value and find array in a depth (array in an array)
    } 
      return (allCategory = ["All", ...new Set(allCategory)]); //remove dublicate value

  };

  //we need category data//
  const categoryData = getUniqueData(all_products, "category");
  const companyData = getUniqueData(all_products, "company");
  const colorData = getUniqueData(all_products, "colors");
  // console.log(colorData)

  return (
    <>
      <Wrapper>
        {/* search input */}
        <div className="filter_section">
          <form onSubmit={(e) => e.preventDefault()}>
            <input
              type="text"
              placeholder="search"
              name="text"
              value={text}
              onChange={updateFilterValue}
            />
          </form>

          {/* category */}
          <div className="category">
            <h2>category</h2>
            <div>
              {categoryData.map((currentCategory, index) => 
              {
                //console.log(currentCategory)
                return (
                  <button
                    key={index}
                    name="category"
                    type="button"
                    value={currentCategory}
                    className={currentCategory === category ? "active" : ""}
                    onClick={updateFilterValue}
                  >
                    {currentCategory}
                  </button>
                );
              })}
            </div>

            {/* company */}
            <div className="company">
              <h2>Company</h2>
              <form action="#">
                <select onClick={updateFilterValue} name="company" id="company">
                  {companyData.map((currentData, index) => {
                    console.log(currentData)
                    return (
                      <option key={index} value={currentData} name="company">
                        {currentData}
                      </option>
                    );
                  })}
                </select>
              </form>
            </div>
            {/* color */}
            <div className="color-section">
              <h2>color</h2>
              <div className="color">
                {colorData.map((currentColor, index) => {

                  if(currentColor === "all"){
                    return (
                    <button
                      key={index}
                      name="color"
                      type="button"
                      // className="all_btn_style"
                      value={currentColor}
                      onClick={updateFilterValue}
                    >
                     All
                    </button>
                  );
                  }

                  return (
                    <button
                      key={index}
                      name="color"
                      type="button"
                      className={
                        currentColor === "color_btn"
                          ? "color_btn active"
                          : "color_btn"
                      }
                      value={currentColor}
                      style={{ backgroundColor: currentColor }}
                      onClick={updateFilterValue}
                    >
                      {/* {currentColor} */}
                      {color === currentColor ? <DoneIcon className="tick"/> : null}
                    </button>
                  );
                })}
              </div>
            </div>

            {/* price */}
            <div className="price_section">
              <h2>Price</h2>
              <p>80000</p>
            </div>

            <div>
              <Button style={{ background: "#E24461" }}>CLEAR FILTERS</Button>
            </div>
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  //category//
  .category {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;

    button {
      display: flex;
      flex-direction: column;
      border: none;
      background: transparent;
      gap: 1.5rem;
    }
    .active {
      border-bottom: 1px solid grey;
      color: ${({ theme }) => theme.colors.text};
    }
  }
  .color-section {
    .color {
      display: flex;
      flex-direction: row;
      align-items: center;
      justify-content: flex-start;
      gap: 2.5rem;
      margin: 1rem 0;
      .color_btn {
        width: 2rem;
        height: 2rem;
        border-radius: 50%;
        opacity: 0.7;
        margin-left: -2rem;
        &:hover {
          opacity: 1;
        }
      }
      .active {
        opacity: 1;
      }
      .tick{
        color:white;
        margin:.3rem 0 0 .2rem;
      }

    }
  }
`;
export default FilterSection;
