import { useState } from "react";
import styled from "styled-components";
import CheckIcon from "@mui/icons-material/Check";
import { useProductContext } from "./context/productContext";
import Count from "./components/Count";
import { NavLink } from "react-router-dom";
import { Button } from "./Button";

const AddToCart = ({ product }) => {
  const { singleProduct } =
  useProductContext();
  const { id, colors, stock } = product;
  const [color, setColor] = useState(colors[2]);

  return (
    <Wrapper>
      <div className="colors">
        <p>
          Colors:
          {colors.map((currentColor, index) => {
            return (
              <button
                key={index}
                style={{ background: currentColor }}
                className={
                  color === currentColor ? "color_btn  active" : "color_btn"
                }
                onClick={() => setColor(currentColor)}
              >
                {color === currentColor ? <CheckIcon className="tick" /> : null}
              </button>
            );
          })}
        </p>
      </div>

 {/* cart count and add to cart button on count page*/}
 <Count IncDec={singleProduct} product={singleProduct}  />

          {/* add to cart button */}
          <NavLink to="/cart" >
         <Button className="addtocart_btn">Add To Cart</Button>
         </NavLink>
    </Wrapper>
  );
};
const Wrapper = styled.section`
  .color_btn {
    width: 2rem;
    height: 2rem;
    border: none;
    border-radius: 50%;
    margin-top: 1rem;
    margin-left: 1rem;
    opacity: 0.5;

    &:hover {
      opacity: 1;
    }
  }
  .active {
    opacity: 1;
  }
  .tick {
    color: white;
  }
  .addtocart_btn{
    margin:-.1rem 0 0 0 ;
  }
`;
export default AddToCart;
