import React from "react";
import styled from "styled-components";
import Count from "./Count";
import FormatPrice from "./FormatPrice";
const CartItem = ({ id, name, image, price, count, color }) => {
  return (
    <Wrapper>
      <div className="cart_heading grid-five-column">
        <div className="cart_image_name grid_two_column ">
          <img src={image} alt={name} />
          <div>
            <p>{name}</p>
            <div className="color_div">
              <p>Color:</p>
              <div
                className="color_style"
                style={{ backgroundColor: color, color: color }}></div>
            </div>
          </div>
          <div className="hide-price">
            <p>
              <FormatPrice price={price} />
            </p>
          </div>
          <div className="">
            <p>
              <Count count={count} />
            </p>
          </div>
        </div>
      </div>
    </Wrapper>
  );
};
const Wrapper = styled.section`
.container {
  max-width: 90rem 0;
  margin: 10rem 20rem 10rem 20rem;

  .grid-five-column {
    display: grid;
    gap: 16rem;
    grid-template-columns: repeat(5, 1re);
  }
}
