import React from "react";
import { useFilterContext } from "../context/Filter_Context";
import { Button } from "./Button";
import styled from "styled-components";

const FilterSection = () => {
  const {
    filters: { text, category , company , color},
    all_products,
    updateFilterValue,
  } = useFilterContext();

  //to get unique data of all field//
  const getUniqueData = (data, property) => {
    let allCategory = data.map((currentData) => {
      return currentData[property];
    });

    if(property === "colors"){
      return (allCategory = ["All", ...new Set([].concat(...allCategory))]); //remove dublicate value
      // console.log(allCategory)
    }else{

      return (allCategory = ["All", ...new Set(allCategory)]); //remove dublicate value
    }
  };


  //we need category data//
  const categoryData = getUniqueData(all_products, "category");
  const companyData = getUniqueData(all_products, "company");
  const colorData = getUniqueData(all_products, "colors");
  // console.log(colorData)

  return (
    <>
      <Wrapper>
        {/* search input */}
        <div className="filter_section">
          <form onSubmit={(e) => e.preventDefault()}>
            <input
              type="text"
              placeholder="search"
              name="text"
              value={text}
              onChange={updateFilterValue}
            />
          </form>

        {/* category */}
        <div className="category">
          <h2>category</h2>
          <div>
            {categoryData.map((currentData, index) => {
              return (
                <button
                  key={index}
                  name="category"
                  type="button"
                  value={currentData}
                  className={currentData === category ? "active" : ""}
                  onClick={updateFilterValue}
                >
                  {currentData}
                </button>
              );
            })}
          </div>

          {/* company */}
          <div className="company">
            <h2>Company</h2>
            <form action="#">
              <select onClick={updateFilterValue} name="company" id="company">
                {companyData.map((currentData, index) => {
                  return (
                    <option key={index} value={currentData} name="company">
                      {currentData}
                    </option>
                  );
                })}
              </select>
            </form>
          </div>
          {/* color */}
          <div className="color">
          <h2>color</h2>
          <div>
            {colorData.map((currentData, index) => {
              return (
                <button
                  key={index}
                  name="color"
                  type="button"
                  className="color_btn"
                  value={currentData}
                 style={{backgroundColor:currentData}}
                  onClick={updateFilterValue}
                >
                {currentData}
                  {/* {color === currentData ? "" :null} */}

                </button>
              );
            })}
          </div>

          {/* price */}
          <div>
            <h2>Price</h2>
            <p>80000</p>
          </div>

          <div>
            <Button style={{ background: "#E24461" }}>CLEAR FILTERS</Button>
          </div>
        </div>
        </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  //category//
  .category {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;

    button {
      display: flex;
      flex-direction: column;
      border: none;
      background: transparent;
      gap: 1.5rem;
    }
    .active {
      border-bottom: 1px solid grey;
      color: ${({ theme }) => theme.colors.text};
    }
    .color{
      display:flex;
      align-items:color-interpolation-filters;

      .color_btn{
      width:2rem;
      height:2rem;
      border-radius:50%;

    }
    }
  }
`;
export default FilterSection;
