import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { Button } from "./components/Button";
import GoogleIcon from "@mui/icons-material/Google";
import FacebookOutlinedIcon from "@mui/icons-material/FacebookOutlined";
import TwitterIcon from "@mui/icons-material/Twitter";
const Footer = () => {
  return (
    <>
      <Wrapper>  
        <footer>
          <div className="footer_portion">
            <div className="container">
              <div className="grid grid-three-column">
                  <h3>follow us</h3>

                </div>
                <div className="">
                  <h3>contact us:</h3>
                  <a href="tel:+918755231772">+918755231772</a>
                </div>
              </div>
            </div>
            <hr className="footer_hr" />
            <div className="container hr_bottom grid-two-colums">
              <div className="">
                <p>
                  ©️{new Date().getFullYear()} Grocery Store.All Rights Reserved
                </p>
              </div>
              <div className=" bottom">
                <p>PRIVACY POLICY</p>
                <p>TERMS & CONDITION</p>
              </div>
            </div>
        </footer>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  margin-top: 9rem;

  .footer_portion {
    padding: 1rem 0 7rem 0;
    background: ${({ theme }) => theme.colors.bg};
    color: white;
    .container {
      text-transform: capitalize;
      line-height: 3.4rem;
      .grid-four-column {
        .grid-two-rows {
          h3 {
            font-size: 1.7rem;
          }
          a {
            text-decoration: none;
            list-style: none;
            color: white;
            font-size: 1.4rem;
            cursor: pointer;
          }
          p {
            color: white;
            font-size: 1.4rem;
            line-height: 1.5rem;
          }

          .icons {
            padding: 0.1rem;
            cursor: pointer;
            .icon {
              border: 1px solid white;
              margin-left: 0.6rem;
              border-radius: 50%;
              font-size: 1.7rem;
              cursor: pointer;
            }
          }
          .footer_btn {
            padding: 0.3rem 1rem;
          }
        }
      }
    }
    .footer_hr {
      color: ${({ theme }) => theme.colors.bg};
      height: 0.01rem;
      margin: 2rem 0;
    }
    .hr_bottom {
      display: flex;
      align-items: center;
      justify-content: space-around;

      p {
        color: white;
        font-size: 1.2rem;
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.tab}) {
    .container {
      max-width: 120rem;
      padding: 0 3.2rem;
    }
    .footer_portion {
      .container {
        display: flex;
        align-items: center;
        justify-content: center;
        margin: auto;

        .grid {
          display: grid;
          gap: 4rem;
          margin: 8rem 12rem 0rem 12rem;

          .grid-four-column {
            grid-template-columns: 1.4fr 1.2fr 1.3fr 0.8fr;
            .grid-two-rows .bottom {
              display: flex;
              align-items: center;
              justify-content: space-between;
              margin-left: 4rem;
            }
          }
        }
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.mobile}) {
    .footer_portion {
      .container {
        display: flex;
        align-items: center;
        justify-content: center;
        margin: auto;
        .grid {
          display: grid;
          gap: 1.6rem;
          margin: 8rem 12rem 0rem 12rem;

        .grid-four-column {

          .grid-two-rows .bottom {
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin-left: 4rem;
          }
        }
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.small_phone}) {
    .container {
    .box {
        display:flex;
        justify-content:space-around;
        align-items:center;
        font-size:1.2rem;

.box_content{
    p{ font-size:1.4rem;}

   .box_btn{
padding:0.3rem 1rem;
       font-size:1.2rem;
       margin-bottom:2.6rem;
   }
}
  }
}
.icons {
       display:flex;

       flex-direction:column;
            cursor: pointer;
            .icon {
              border: 1px solid white;
              margin-left: 0.6rem;
              border-radius: 50%;
              font-size: 1.7rem;
              cursor: pointer;
            }
          }
  }
`;
export default Footer;
