import React from 'react'

const productReducer = (state,action) => {
if (action.type === "SET_LOADING"){
  return{
    ...state,
    isLoading:true,
  }
}
if (action.type === "API_ERROR"){
  return{
    ...state,
    isLoading:false,
    isError:true,

  }
} 
return state;

}

export default productReducer
