import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import FormatPrice from "./FormatPrice";
import { Description } from "@mui/icons-material";

const ListView = ({ products }) => {
  return (
    <>
      <Wrapper>
        {products.map((currentData) => {
          const { id, name, image, price, category,description } = currentData;
          return (
            <>
              <div className="list_view_data">
                  <div className="grid grid_two_column">
                <NavLink to={`/singleproduct/${id}`}>
                    <div className="card" key={id}>
                      <figure>
                        <img className="product_img" src={image} alt={name} />
                        <figcaption className="product_caption">
                          {category}
                        </figcaption>
                      </figure>
                    </div>

                </NavLink>

                <div>
                  <div className="card_data">
                    <div className="class_data_flex">
                      <h3>{name}</h3>
                      <p className="price_format">
                        <FormatPrice price={price} />
                      </p>
                      <p>{description.slice(0,90)}.....</p>
                    </div>
                  </div>
                </div>
              </div>
              </div>
            </>
          );
        })}
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  .list_view_data {
    width: 100%;
    border: 0.2px solid grey;
    margin: 2rem;
  }
  .grid {
    display: grid;
    gap: 3rem;
    margin:1rem 2rem 0rem 2rem;
  }
 figure{ 
    .product_img{
    width:20rem;
    height:14rem;
  }}
  .card_data{
    h3{
        font-size:1.3rem;
    }
  }
`;
export default ListView;
