const FilterReducer = (state, action) => {
  switch (action.type) {
    case "LOAD_FILTER_PRODUCTS":
      const priceArr = action.payload.map((currentPrice) => currentPrice.price);
       console.log(Math.max.apply(priceArr));//apply mean it cn convert an array in an integer


      return {
        ...state,
        filter_products: [...action.payload],
        all_products: [...action.payload],
      };

    case "SET_GRID_VIEW":
      return {
        ...state,
        grid_view: true,
      };
    case "SET_LIST_VIEW":
