    case "UPDATE_FIlTER_VAlUE":
      const { name, value } = action.payload;

      return {
        ...state,
        filters: {
          ...state.filters,
          [name]: value,
        },
      };

    case "FILTER_PRODUCTS":
      let { all_products } = state;
      let tempFilterProduct = [...all_products];

      const { text , category ,company, color} = state.filters;

      if (text) {
        tempFilterProduct = tempFilterProduct.filter((currentData) => {
          return currentData.name.toLowerCase().includes(text);
        });
      }
      if (category !== "all") {
        tempFilterProduct = tempFilterProduct.filter((currentCategory) => {
          return currentCategory.category === category;
        });
      }
      if (company !== "all") {
        tempFilterProduct = tempFilterProduct.filter((currentCompany) => {
          return currentCompany.company.toLowerCase().includes(company);
        });
      }
      if (color !== "all") {
        tempFilterProduct = tempFilterProduct.filter((currentColor) => {
          return currentColor.colors.includes(color);
        });
      }
      return {
        ...state,
        filter_products: tempFilterProduct,
      };
    default:
      return state;
  }
};

export default FilterReducer;
