import {useState} from 'react';
import styled from "styled-components";
import CheckIcon from '@mui/icons-material/Check';

const AddToCart = ({product}) => {
    const {id , colors , stock} = product;
    const [ color, setColor] = useState(colors[2]);

  return (
    <Wrapper>
<div className='colors'>
    <p>
    Colors:
    {
        colors.map((currentColor , index) =>{
            return <button key={index} style={{ background : currentColor }} className={color === currentColor ? "color_btn  active" : "color_btn"}
            onClick={() =>setColor(currentColor)}
            >
            {color === currentColor ? <CheckIcon className='tick'/> :null}
            </button>
        })
    }

    </p>
</div>
    </Wrapper>
  )
}
const Wrapper = styled.section`
.color_btn{
    width:2rem;
    height:2rem;
    border: none;
    border-radius:50%;
    margin-top:1rem;
    margin-left:1rem;
    opacity:.5;

    &:hover{
        opacity:1;
    }
}
.active{
        opacity:1;
    }
    .tick{
        color: white;
    }
`;
export default AddToCart;
