import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { Button } from "./components/Button";
import GoogleIcon from "@mui/icons-material/Google";
import FacebookOutlinedIcon from "@mui/icons-material/FacebookOutlined";
import TwitterIcon from "@mui/icons-material/Twitter";
const Footer = () => {
  return (
    <>
      <Wrapper>
      <div className="container box">

      </div>
        <div className="footer_portion">
          <div className="container">
            <div className="grid grid-four-column">
              <div className="grid-two-rows">
                <h3>grocery store🛒</h3>
                <p>The best online grocery store in India.</p>
              </div>
              <div className="grid-two-rows">
                <input
                  type="email"
                  placeholder="Your Email"
                  autocomplete="off"
                  required
                />
                <NavLink>
                  <Button className="footer_btn">SEND</Button>
                </NavLink>
              </div>
              <div className="grid-two-rows">
                <h3>follow us</h3>
                <div className="icons">
                  <GoogleIcon className="icon" />
                  <FacebookOutlinedIcon className="icon" />
                  <TwitterIcon className="icon" />
                </div>
              </div>
              <div className="grid-two-rows">
                <h3>contact us:</h3>
                <p>+918755231771</p>
                <h3>EmailID:</h3>
                <p>kajal@gmail.com</p>
              </div>
            </div>
          </div>
          <hr className="footer_hr"/>
         <div className="container hr_bottom grid-two-colums"> 
         <div className="grid-two-rows">
            <p>©️2022 Grocery Store.All Rights Reserved</p>
          </div>
          <div className="grid-two-rows">
            <p>PRIVACY POLICY</p>
            <p>TERMS & CONDITION</p>
          </div>
         </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
margin-top:9rem;
.box{
    background: ${({ theme }) => theme.colors.bg};
    width:80rem;
    height:7rem;
    position:absolute;
    top:128rem;
    left:21%;
    z-index:4;
}
  .footer_portion {
    position:relative;
    z-index:-2;
    padding:1rem 0 7rem 0;
    background: ${({ theme }) => theme.colors.text};
    color: white;
    .container {
      text-transform: capitalize;
      line-height: 3.4rem;
      .grid-four-column {
        .grid-two-rows {
          h3 {
            font-size: 1.7rem;
          }
          p {
            color: white;
            font-size: 1.4rem;
            line-height: 1.5rem;
          }
          .icons {
            padding: 0.1rem;

            .icon {
              border: 1px solid white;
              margin-left: 0.6rem;
              border-radius: 50%;
              font-size: 1.7rem;
            }
          }
          .footer_btn{
            padding:0.3rem 1rem;
          }
        }
      }
    }
    .footer_hr{
        color:${({ theme }) => theme.colors.bg};
        height:.01rem;
        margin:3rem 0;
    }
    .hr_bottom{
        display:flex;
        align-items:color-interpolation-filters;
        p{
            color:white;
            font-size: 1.2rem;
        }
    }
  }
`;
export default Footer;
