import React from "react";

import GridViewIcon from "@mui/icons-material/GridView";
import ViewListIcon from "@mui/icons-material/ViewList";
import styled from "styled-components";
import { useFilterContext } from "../context/Filter_Context";

const Sort = () => {
  const { grid_view, setGridView, setListView ,filter_products} = useFilterContext();
  return (
    <>
      <Wrapper>
        <div className="grid_list_view">
        {/* first column */}
          <div className="sorting_list_grid">
          <button
            className={grid_view ? "active sort-btn" : "sort-btn"}
            onClick={setGridView}
          >
            <GridViewIcon className="gird_view" />
          </button>
          <button
            className={!grid_view ? "active sort-btn" : "sort-btn"}
            onClick={setListView}
          >
            <ViewListIcon className="list_view" />
          </button>
          </div>
            {/* second column */}
          <div>
            <p>{`${filter_products.length} Product Available`}</p>
          </div>
          {/* third column */}
          <div className="dropdown">
            <form action="#">
              <label htmlFor="sort"></label>
              <select name="products" id="sort">
              <option value="lowest">Price(lowest)</option>
              <option value="highest">Price(highest)</option>
              <option value="lowest">Price(a-z)</option>
              <option value="lowest">Price(z-a)</option>
            </select>
            </form>
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  .grid_list_view {
    display:flex;
    justify-content:space-around;
    align-items:center;
   margin-bottom:4rem;

    .sorting_list_grid{
      display:flex;
      gap:2rem;
    .sort-btn {
      color:${({theme}) => theme.colors.text};
      border: 1px solid ${({theme}) => theme.colors.text};
      background: white;
      padding: .6rem;
      cursor: pointer;
    } 
      .active {
      color:white;
      background: ${({theme}) => theme.colors.text};
      border: none;
      }
      .gird_view,
      .list_view {
        font-size: 2.4rem;
        display: flex;
        align-items: center;
        flex-direction: column;
    }
  }
}
.dropdown{
padding:10rem;
}

`;
export default Sort;
