import React, { useState } from "react";
import styled from "styled-components";
import { ChooseUs_Card_Detail } from "../../ChooseUs_Card_Detail";

const Vertical_Card = () => {
  const [state, setState] = useState(ChooseUs_Card_Detail);
  return (
    <>
      <Wrapper>
        <div class="container">
        <div className="content">
        <h1>Why Choose Us</h1>
        <p>Here are the reasons you can depend on Ftechiz Solutions to keep you and your business up and running:</p>
        </div>
          <div class="grid ">
            {state.map((currentData) => {
              const { icon, title, para } = currentData;
              return (
                <>
                  <div class="card">
                    <div class=" grid-two-column">
                      <div className="card_icon">
                        <div className="icon">{icon}</div>
                      </div>
                      <div class="card-body">
                        <h3 class="card-title">{title}</h3>
                        <p class="card-text" style={{ textAlign: "justify" }}>
                          {para}
                        </p>
                      </div>
                    </div>
                  </div>
                </>
              );
            })}
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  padding: 3rem 0;
  .grid {
    display: grid;
    gap: 1rem;
    margin: 8rem 0rem;
  }
  .content{
    margin-top:3rem;
    text-align:center;
  }
  .card {
    width: 100%;
    display: flex;
    flex-direction: row;
    background: ${({ theme }) => theme.colors.services_card_bg};
    border-radius: 1rem;
    h3 {
      margin: 1rem 0rem;
    }
    .grid-two-column {
      display: flex;
      flex-direction: row;
      grid-template-columns: 1fr 1fr;
      .card_icon {
        display: inline-block;
        padding: 3rem 1rem;
        margin: auto;
      }
    }
  }
`;
export default Vertical_Card;
