import styled from "styled-components";

export const Button = styled.button`
text-decoration:none;
color:#061849;
text-transform: uppercase;
font-size: 1.4rem;
font-weight: 500;
border:none;
padding: 0.6rem;
background: ${({theme}) =>theme.colors.bg}
`;
