import React from "react";
import { useFilterContext } from "../context/Filter_Context";
import { Button } from "./Button";
import Category from "./Category";
import styled from "styled-components";

const FilterSection = () => {
  const {
    filters: { text },
    all_products,
    updateFilterValue,
  } = useFilterContext();

  //to get category data//
  const getCategoryData = (data, property) => {
    let allCategory = data.map((currentData) => {
      return currentData[property];
    });
   return(allCategory = ["All", ...new Set(allCategory)]);
    // console.log(allCategory);
  };

  //we need category data//
  const category = getCategoryData(all_products, "category");

  return (
    <>
      <Wrapper>
      {/* search input */}
      <div className="filter_section">
        <form onSubmit={(e) => e.preventDefault()}>
          <input
            type="text"
            placeholder="search"
            name="text"
            value={text}
            onChange={updateFilterValue}
          />
        </form>

        <div className="category">
            <h2>category</h2>
        </div>
        <Category getCategoryDat={getCategoryData} />
        <div className="company">
          <h2>Company</h2>
          <input type="text" placeholder="" />
        </div>
        <div>
          <h2>Colors</h2>
          <p>All</p>
        </div>
        <div>
          <h2>Price</h2>
          <p>80000</p>
        </div>

        <div>
          <Button style={{ background: "#E24461" }}>CLEAR FILTERS</Button>
        </div>
      </div>
      </Wrapper>
    </>
  );
};

export default FilterSection;
