import React from "react";

import GridViewIcon from "@mui/icons-material/GridView";
import ViewListIcon from "@mui/icons-material/ViewList";
import styled from "styled-components";
import { useFilterContext } from "../context/Filter_Context";

const Sort = () => {
  const { grid_view, setGridView, setListView } = useFilterContext();
  return (
    <>
      <Wrapper>
        <div className="grid_list_view">
          <div className="sorting_list_grid">
          <button
            className={grid_view ? "active sort-btn" : "sort-btn"}
            onClick={setGridView}
          >
            <GridViewIcon className="gird_view" />
          </button>
          <button
            className={!grid_view ? "active sort-btn" : "sort-btn"}
            onClick={setListView}
          >
            <ViewListIcon className="list_view" />
          </button>
          </div>

          <div>
            <p>{` ${ filter_products }total 12 products`}</p>
          </div>
          <div className="dropdown">
            <select name="products" id="things">
              <option value="watch"></option>
              <option value="lappy"></option>
            </select>
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  .grid_list_view {
    display:flex;
    justify-content:space-between;
    align-items:center;
   margin-bottom:4rem;

    .sorting_list_grid{
      display:flex;
      gap:2rem;
    .sort-btn {
      color:${({theme}) => theme.colors.text};
      border: 1px solid ${({theme}) => theme.colors.text};
      background: white;
      padding: .6rem;
      cursor: pointer;
    } 
      .active {
      color:white;
      background: ${({theme}) => theme.colors.text};
      border: none;
      }
      .gird_view,
      .list_view {
        font-size: 2.4rem;
        display: flex;
        align-items: center;
        flex-direction: column;
    }
  }
}
.dropdown{
width:10rem;
}

`;
export default Sort;
