// import { AccessibilityOutlined } from "@mui/icons-material";

const cartReducer = (state, action) => {
  if (action.type === "ADD_TO_CART") {
    let { id, color, count, product } = action.payload;
    // console.log(product);

    //existing product//
    let existingProduct = state.cart.find((currentData) => {
      return currentData.id === id + color;
    });

    if (existingProduct) {
      let updatedProduct = state.cart.map((currentData) => {
        if (currentData.id === id + color) {
          let newCount = currentData.count + count;

          if (newCount >= currentData.max) {
            newCount = currentData.max;
          }
          return {
            ...currentData,
            count: newCount,
          };
        } else {
          return currentData;
        }
      });
      return {
        ...state,
        cart: updatedProduct,
      };
    } else {
      let cartProducts;

      cartProducts = {
        id: id + color,
        color,
        count,
        image: product.image[0].url,
        name: product.name,
        price: product.price,
        max: product.stock,
      };

      return {
        ...state,
        cart: [...state.cart, cartProducts],
      };
    }
  }

  // set increment and decrement//

  if (action.type === "SET_DECREMENT") {
    let updatedProduct = state.cart.map((currentData) => {
      if (currentData.id === action.payload) {
        let decCount = currentData.count - 1;
        if (decCount <= 0) {
          decCount = 1;
        }
        return {
          ...currentData,
          count: decCount,
        };
      } else {
        return currentData;
      }
    });
    return {
      ...state,
      cart: updatedProduct,
    };
  }

  if (action.type === "SET_INCREMENT") {
    let updatedProduct = state.cart.map((currentData) => {
      if (currentData.id === action.payload) {
        let incCount = currentData.count + 1;
        if (incCount >= currentData.max) {
          incCount = currentData.max;
        }
        return {
          ...currentData,
          count: incCount,
        };
      } else {
        return currentData;
      }
    });
    return {
      ...state,
      cart: updatedProduct,
    };
  }
  // delete items//
  if (action.type === "DELETE_ITEM") {
    let updateCart = state.cart.filter((currentData) => {
      return currentData.id !== action.payload;
    });

    return {
      ...state,
      cart: updateCart,
    };
  }
  //clear cart btn //
  if (action.type === "CLEAR_CART") {
    return {
      ...state,
      cart: [],
    };
  }
  //nav total cart items
  // if (action.type === "CART_TOTAL_ITEM") {
  //   let newUpdatedItemValue = state.cart.reduce((initialValue, currentData) => {
  //     let { count } = currentData;

  //     initialValue = initialValue + count;
  //     return initialValue;
  //   }, 0);

  //   return {
  //     ...state,
  //     total_items: newUpdatedItemValue,
  //   };
  // }
  //sub_total of cart items
  if (action.type === "CART_SUBTOTAL") {
    let total_price = state.cart.reduce((initialValue, currentData) => {
      let { price , count } = currentData;

      initialValue = initialValue + price*count;
      return initialValue;
    }, 0);

    return {
      ...state,
      total_price,
    };
  }

  return state;
};

export default cartReducer;
