import React from "react";

import GridViewIcon from "@mui/icons-material/GridView";
import ViewListIcon from "@mui/icons-material/ViewList";
import styled from "styled-components";

const Sort = ({grid_view,setGridView,setListView}) => {
  return (
    <>
      <Wrapper>
        <div className="grid_list_view">
            <button className={grid_view ? "active sort-btn" : "sort-btn"} onClick={setGridView} >
            <GridViewIcon className="gird_view" />
            </button>
           <button  className={!grid_view ? "active sort-btn" : "sort-btn"} onClick={setGridView}>
           <ViewListIcon  className="list_view" />
           </button>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  .grid_list_view {
    margin-top: 1rem;
    .gird_view,
    .list_view {
      font-size: 2.4rem;
      margin-left: 1rem;
    }
    .sort-btn{
      color:white;
      background: black;
    }
  }
`;
export default Sort;
