import { useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from '@mui/icons-material/Remove';
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import { Button } from "./Button";
import CheckIcon from "@mui/icons-material/Check";

const AddToCart = ({ product }) => {
  const { id, colors, stock } = product;
  const [color, setColor] = useState(colors[2]);
  const [count , setCount] = useState(1);


  const INC = ()=>{
    count < stock ? setCount(count+1) : setCount(stock);
  }
  const DEC = ()=>{
    count > 1 ? setCount(count-1) : setCount(1);
  }
  const addTocart = () => {}

  return (
    <Wrapper>
      <div className="colors">
        <p>
          Colors:
          {colors.map((currentColor, index) => {
            return (
              <button
                key={index}
                style={{ background: currentColor }}
                className={
                  color === currentColor ? "color_btn  active" : "color_btn"
                }
                onClick={() => setColor(currentColor)}
              >
                {color === currentColor ? <CheckIcon className="tick" /> : null}
              </button>
            );
          })}
        </p>
      </div>

      {/* count (increment and decrement) */}
      <div className="count">
          <div className="count_btn">
            <AddIcon className="count_icon"  onClick={INC}/>
            <p className="count_icon">{count}</p>
            <RemoveIcon className="count_icon"  onClick={DEC}/>
          </div>

          {/* add to cart button */}
         <NavLink to="/cart" onClick={() => addTocart( )}>
           <Button className="addtocart_btn">Add To Cart</Button>
         </NavLink>
        </div>
    </Wrapper>
  );
};
const Wrapper = styled.section`
  .color_btn {
    width: 2rem;
    height: 2rem;
    border: none;
    border-radius: 50%;
    margin-top: 1rem;
    margin-left: 1rem;
    opacity: 0.5;

    &:hover {
      opacity: 1;
    }
  }
  .active {
    opacity: 1;
  }
  .tick {
    color: white;
  }
`;
export default AddToCart;
