import { Button } from "./Button";
import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
const Main_Section = ({myData ,para}) => {
const {name} = myData ;
const {paraData} = para;
  return (
    <>
      <Wrapper>
        <div className="container">
          <div className="grid grid_two_column">
            <div className="main_section">
              <p className="para">welcome to</p>
              <h1 className="heading">{name}</h1>
              <p>
              {paraData}
              </p>
              <NavLink>
                <Button>shop now</Button>
              </NavLink>
            </div>
            <div classname="main_section_image">
                <figure>
                <img src="./images/main_section_image.jpg" alt="main_section_image" className="image"/>
                </figure>
            </div>
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
padding:12rem 0;
img {
    min-width: 20rem;
    height: 20rem;
  }
      .main_section_image{
          width:100%;
          height:auto;
          display:flex;
          justify-content:center;
          align-items:center;
      }
          figure{
              position:relative;

              &::after{
                  content:"";
                  width:80%;
                  height:70%;
                  background: ${({theme}) =>theme.colors.helper};
                  position:absolute;  
                  top:-6rem;
                  left:10%;
                  z-index:-1;
                  opacity:.7;
                }
            }
            .image{
                width:80%;
                height:auto;
              }  




`; // 1fr=25% of the available space.
export default Main_Section;
