import {useState} from "react";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from '@mui/icons-material/Remove';
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import { Button } from "./Button";

const Cart = ({IncDec}) => {
  const { stock} = IncDec
  const [count , setCount] = useState(1);
  const INC = ()=>{
    count < stock ? setCount(count+1) : setCount(stock);
  }
  const DEC = ()=>{
    count > 1 ? setCount(count-1) : setCount(1);
  }
  return (
    <>
      <Wrapper>
        <div className="count">
          <div className="count_btn">
            <AddIcon className="count_icon"  onClick={INC}/>
            <p className="count_icon">{count}</p>
            <RemoveIcon className="count_icon"  onClick={DEC}/>
          </div>
         <NavLink to="/cart">
         <Button className="addtocart_btn">Add To Cart</Button>
         </NavLink>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  padding: .5rem 0;
  .count{
  .count_btn {
    color: ${({ theme }) => theme.colors.text};
    display: flex;
    align-items: center;
    justify-content: flex-start;
    gap: 1rem;

    .count_icon {
      font-size: 2rem;
    }

  }
  .addtocart_btn{
    margin-bottom:-2rem;
  }
  }
`;
export default Cart;
