import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { Button } from "./components/Button";
import CallIcon from "@mui/icons-material/Call";
import EmailIcon from "@mui/icons-material/Email";
import LanguageSharpIcon from "@mui/icons-material/LanguageSharp";
const Footer = () => {
  return (
    <>
      <Wrapper>
        <footer>
          <div className="footer_portion">
            <div className="container">
              <div className="grid grid-three-column">
                {/* contact info start */}
                <div className="contact_portion">
                  <h3>Office Location</h3>
                  <p>
                    3rd Floor Pitambar Plaza, Near Canara Bank Haridwar Road,
                    shastri nagar Dehradun, Uttrakhand 248001 INDIA
                  </p>
                  <h3>Contact Details</h3>
                  <p>
                    <CallIcon />{" "}
                    <NavLink to="tel:+918755231772">+918755231772</NavLink>
                  </p>
                  <p>
                    <EmailIcon /> <NavLink to="#"> sales@ftechiz.com</NavLink>
                  </p>
                  <p>
                    <LanguageSharpIcon />
                    <NavLink to="#">www.ftechiz.com</NavLink>
                  </p>
                  <div className="icons">
                    <NavLink to="#">
                      <i class="fa-brands fa-square-google-plus"></i>
                    </NavLink>
                    <NavLink to="#">
                      <i class="fa-brands fa-square-facebook"></i>
                    </NavLink>
                    <NavLink to="#">
                      <i class="fa-brands fa-square-twitter"></i>
                    </NavLink>
                    <NavLink to="#">
                      <i class="fa-brands fa-linkedin"></i>
                    </NavLink>
                  </div>
                  <div>
                    <iframe
                      src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3445.078763705736!2d78.0581474152188!3d30.291819281792982!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x390929c315555515%3A0x25553e3289a692dd!2sFtechiz%20Solutions%20Pvt%20Ltd!5e0!3m2!1sen!2sin!4v1669802354380!5m2!1sen!2sin"
                      width="400"
                      height="150"
                      style={{ border: 0, borderRadius: "1rem" }}
                      allowFullScreen=""
                      loading="lazy"
                      referrerPolicy="no-referrer-when-downgrade"
                    ></iframe>
                  </div>
                </div>
                {/* contact info end */}

                {/* services start */}

                <div className="services">
                  <ul>
                    <li>
                      <NavLink to="#">blockchain development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">mobile app development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">android app development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">ios app development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">flutter app development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">react native app development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">website development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">website designing company</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">web app development</NavLink>
                    </li>
                    <li>
                      <NavLink to="#">cloud application development</NavLink>
                    </li>
                  </ul>
                </div>

                {/* services end */}
              </div>
            </div>
          </div>

          {/* <div className="container hr_bottom grid-two-colums">
              <div className="">
                <p>
                  ©️{new Date().getFullYear()} Grocery Store.All Rights Reserved
                </p>
              </div>
              <div className="">
                <p>PRIVACY POLICY</p>
                <p>TERMS & CONDITION</p>
              </div>
            </div> */}
        </footer>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  margin-top: 9rem;

  .footer_portion {
    padding: 1rem 0 7rem 0;
    background: ${({ theme }) => theme.colors.bg};
    color: white;
    .container {
      text-transform: capitalize;
      line-height: 3.4rem;
      .grid-four-column {
        .grid-two-rows {
          h3 {
            font-size: 1.7rem;
          }
          a {
            text-decoration: none;
            list-style: none;
            color: white;
            font-size: 1.4rem;
            cursor: pointer;
          }
          p {
            color: white;
            font-size: 1.4rem;
            line-height: 1.5rem;
          }

          .icons {
            padding: 0.1rem;
            cursor: pointer;
            .icon {
              border: 1px solid white;
              margin-left: 0.6rem;
              border-radius: 50%;
              font-size: 1.7rem;
              cursor: pointer;
            }
          }
          .footer_btn {
            padding: 0.3rem 1rem;
          }
        }
      }
    }
    .footer_hr {
      color: ${({ theme }) => theme.colors.bg};
      height: 0.01rem;
      margin: 2rem 0;
    }
    .hr_bottom {
      display: flex;
      align-items: center;
      justify-content: space-around;

      p {
        color: white;
        font-size: 1.2rem;
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.tab}) {
    .container {
      max-width: 120rem;
      padding: 0 3.2rem;
    }
    .footer_portion {
      .container {
        display: flex;
        align-items: center;
        justify-content: center;
        margin: auto;

        .grid {
          display: grid;
          gap: 4rem;
          margin: 8rem 12rem 0rem 12rem;

          .grid-four-column {
            grid-template-columns: 1.4fr 1.2fr 1.3fr 0.8fr;
            .grid-two-rows .bottom {
              display: flex;
              align-items: center;
              justify-content: space-between;
              margin-left: 4rem;
            }
          }
        }
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.mobile}) {
    .footer_portion {
      .container {
        display: flex;
        align-items: center;
        justify-content: center;
        margin: auto;
        .grid {
          display: grid;
          gap: 1.6rem;
          margin: 8rem 12rem 0rem 12rem;

        .grid-four-column {

          .grid-two-rows .bottom {
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin-left: 4rem;
          }
        }
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.small_phone}) {
    .container {
    .box {
        display:flex;
        justify-content:space-around;
        align-items:center;
        font-size:1.2rem;

.box_content{
    p{ font-size:1.4rem;}

   .box_btn{
padding:0.3rem 1rem;
       font-size:1.2rem;
       margin-bottom:2.6rem;
   }
}
  }
}
.icons {
       display:flex;

       flex-direction:column;
            cursor: pointer;
            .icon {
              border: 1px solid white;
              margin-left: 0.6rem;
              border-radius: 50%;
              font-size: 1.7rem;
              cursor: pointer;
            }
          }
  }
`;
export default Footer;
