import React, { useEffect } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { useProductContext } from "./context/productContext";
import PageNavigation from "./components/PageNavigation";
import MyImage from "./components/MyImage";
import FormatPrice from "./components/FormatPrice";
import LocalShippingIcon from "@mui/icons-material/LocalShipping";
import LoopIcon from "@mui/icons-material/Loop";
import SecurityIcon from "@mui/icons-material/Security";
import Stars from "./components/Stars";
import AddToCart from "./components/AddToCart";
import Count from "./components/Count";

const API = "https://api.pujakaitem.com/api/products";

function SingleProduct() {
  const { getSingleProduct, isSingleLoading, singleProduct } =
    useProductContext();

  const { id } = useParams();

  //destructure
  const {
    id: singlep,
    name,
    company,
    price,
    description,
    category,
    stock,
    stars,
    reviews,
    image,
  } = singleProduct;

  useEffect(() => {
    getSingleProduct(`${API}?id=${id}`);
  }, []);

  if (isSingleLoading) {
    return <div>Loading.....</div>;
  }
  return (
    <>
      <Wrapper>
        <PageNavigation title={name} />
        <div className="container">
          <div className="grid grid_two_column">
            <div className="">
              <div className="product_image">
                <MyImage img={image} />
              </div>
            </div>
            <div className="product_data">
              <h2>{name}</h2>

              <Stars stars={stars} reviews={reviews} />

              <p className="product_data_price">
                MRP:
                <del>
                  <FormatPrice price={price + 250000} />
                </del>
              </p>
              <p className="product_data_price product_data_real_price">
                Deal of the Day: <FormatPrice price={price} />
              </p>
              <p className="product_description">{description}</p>

              <div className="product_data_warranty">
                <div className="product_data_icon">
                  <LocalShippingIcon className="product-icon" />
                  <p>Free Delivery</p>
                </div>

                <div className="product_data_icon">
                  <LoopIcon className="product-icon" />
                  <p>30 Days Replacement</p>
                </div>

                <div className="product_data_icon">
                  <LocalShippingIcon className="product-icon" />
                  <p>Free Delivery</p>
                </div>

                <div className="product_data_icon">
                  <SecurityIcon className="product-icon" />
                  <p>2 Days Warrranty</p>
                </div>
              </div>
              <hr />
              {/* available stock */}
              <div className="product_info">
                <p>
                  Available:
                  <span> {stock > 0 ? "In Stock" : "Not available"}</span>
                </p>
                <p>
                  ID :<span>{id}</span>
                </p>
                <p>
                  {" "}
                  Brand :<span>{company}</span>
                </p>
              </div>
              <hr />
              {/* color */}
              {stock > 0 && <AddToCart product={singleProduct} />}

               {/* cart count */}
              <Count/>

            </div>
          </div>
        </div>
      </Wrapper>
    </>
  );
}
const Wrapper = styled.section`
  color: ${({ theme }) => theme.colors.text};
  .product_data_price {
    font-size: 1.5rem;
  }
  .product_data_real_price {
    font-weight: 600;
  }
  .product_description {
    margin-top: 1.5rem;
    word-spacing: 0.3rem;
    text-align: justify;
    font-size: 1.7rem;
    line-height: 2.4rem;
  }
  .product_data_warranty {
    width: 100%;
    display: flex;
    margin-top: 2rem;
    /* margin-bottom: 1rem; */
    align-items: center;
    justify-content: space-between;
    .product_data_icon {
      text-align: center;
      .product-icon {
        font-size: 2rem;
        background: lightgrey;
        border-radius: 50%;
        padding: -4rem;
      }
      p {
        font-size: 1.37rem;
      }
    }
  }
  .product_info {
    margin-top: 2rem;
    font-size: 1.3rem;
    span {
      font-weight: bold;
    }
  }

`;

export default SingleProduct;
