import {useState} from "react";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from '@mui/icons-material/Remove';
import styled from "styled-components";

const Cart = () => {
  const [count , setCount] = useState(5);
  return (
    <>
      <Wrapper>
        <div className="count">
          <div className="count_btn">
            <AddIcon className="count_icon"  onClick={() => setCount(count+1)}/>
            <p className="count_icon">{count}</p>
            <RemoveIcon className="count_icon"  onClick={() => setCount(count-1)}/>
          </div>
          <button>Add To Cart</button>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  padding: 1.5rem 0;
  .count_btn {
    color: ${({ theme }) => theme.colors.text};
    display: flex;
    align-items: center;
    justify-content: flex-start;
    gap: 1rem;
    margin-left:1rem;

    .count_icon {
      font-size: 2rem;
    }
  }
  button {
    margin: .6rem 0;
    background: ${({ theme }) => theme.colors.helper};
    border: none;
    padding: 0 0.7rem;
  }
`;
export default Cart;
