import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import MenuIcon from '@mui/icons-material/Menu';
import CloseIcon from '@mui/icons-material/Close';

const Navbar = () => {
  const Nav = styled.nav`
    .navbar_list {
      display: flex;
      align-items: center;
      gap: 5rem;

      .navbar_link {
        &:link,
        &:visited {
          font-size: 1.4rem;
          font-weight: 500;
          display: inline-block;
          text-decoration: none;
          list-style: none;
          color:${({theme}) => theme.colors.text};
          transition:color 0.3s linear;
        }
        &:active{
          color:white;
          background-color:${({theme}) => theme.colors.heading};
          border-radius:.5rem;
          cursor: pointer;
        }
        &:hover{
          color:${({theme}) => theme.colors.text};
          opacity:.4;
          cursor: pointer;
        }

      }
      .cart-trolley-link{
          position:relative;

          .cart_trolley{
            position:relative;
            font-size:2.2rem;

          }
         .cart_items{
          opacity:1;
          width: 2.2rem;
          height:2.2rem;
          background-color:#8DA9F1;
          color:${({theme}) => theme.colors.text};
          border-radius:50%;
          display:grid;
          place-items:center;
          position:absolute;
          top:-40%;
          left:70%;
          font-size:1.2rem;
         }
        }
      }
      .mobile_navbar_btn{
        display:none;
        background: transparent;
        cursor: pointer;
        border: none;

        .hamburger_icon{
          display:none;
        }
        .cross_btn{
          display:none;
        }
      }

    @media (max-width:${({theme}) => theme.media.mobile}) {
      .navbar_list {display:none;}

      .mobile_navbar_btn{
        display:inline-block;
        z-index:9999;
        border: ${({theme}) => theme.colors.text};

        .hamburger_icon{
          font-size:4.2rem;
          color:${({theme}) => theme.colors.text};
        }
      }
    }
    .active.hamburger_icon{
      display:none;
      font-size:4.2rem;
      position:absolute;
      top:30%;
      right:10%;
      color:${({theme}) => theme.colors.text};
      z-index:9999;
    }
    .active.cross_btn{
      display:inline-block;
    }
     .navbar_list {
      width:100%;
      height:100%;
      position:absolute;
      top:0;
      left:0;
      background: #fff;
      display: flex;
      align-items: center;
      gap: 5rem;
    }
  `;
  return (
    <>
      <Nav>
        <div className="navbar">
          <ul className="navbar_list">
            <li>
              <NavLink className="navbar_link active" to="/home">
                Home
              </NavLink>
            </li>

            <li>
              <NavLink className="navbar_link" to="/about">
                About
              </NavLink>
            </li>

            <li>
              <NavLink className="navbar_link" to="/contact">
                Contact
              </NavLink>
            </li>

            <li>
              <NavLink className="navbar_link" to="/services">
                Services
              </NavLink>
            </li>
            <li>
              <NavLink className="navbar_link cart-trolley-link" to="/cart">
                <ShoppingCartIcon className="cart_trolley" /> <span className="cart_items">10</span>
              </NavLink>
            </li>
          </ul>
        </div>
        {/* hamburger icon //responsive */}
        <div className="mobile_navbar_btn">
          <MenuIcon className="hamburger_icon"/>
          <CloseIcon className="cross_btn"/>
        </div>
      </Nav>
    </>
  );
};

export default Navbar;
