import React, { useRef } from "react";
import styled from "styled-components";
import { Button } from "./components/Button";
import { NavLink } from "react-router-dom";
// import emailjs from '@emailjs/browser';
function Contact() {
  // const form = useRef();

  // const sendEmail = (e) => {
  //   e.preventDefault();

  //   emailjs.sendForm('service_v49252l', 'template_c3d3g6w', form.current, 'dhnlpBz9g1Zxt4e0I')
  //     .then((result) => {
  //         console.log(result.text);
  //     }, (error) => {
  //         console.log(error.text);
  //     });
  //     e.target.reset();
  // };
  return (
    <>
      <Wrapper>
        <div className="contact_form_portion">
          <h3>contact form</h3>
          <iframe
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d110204.08182051395!2d77.97500897638945!3d30.326000442305173!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3908d7cbdc6e9d1b%3A0x7082f9fac370bdb7!2sPacific%20Mall%20Dehradun!5e0!3m2!1sen!2sin!4v1667299760808!5m2!1sen!2sin"
            width="100%"
            height="300"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
            referrerPolicy="no-referrer-when-downgrade"
          />


         <div className="container">
         <div className="contact-form">
         <form action="https://formspree.io/f/mrgdoqgv" method="POST" className="contact-inputs">
         <input type="text"
           name="user_name"
           placeholder="Enter your Name"
           autocomplete="off"
           required
          />
          <input type="email"
           name="user_email"
           placeholder="Enter your Email"
           autocomplete="off"
           required
          />
          <textarea name="Message"
          cols="30"
          rows="10"
           placeholder="Enter your Message"
           autocomplete="off"
           required
          />
          <NavLink to="/" >
           <Button className="login_btn">Submit</Button>
            </NavLink>
         </form>
         </div>
         </div>
        </div>
      </Wrapper>
    </>
  );
}
const Wrapper = styled.section`
  padding: 5rem 0;

  .contact_form_portion {
    width: 100%;
    height:100%;
    h3 {
      text-align: center;
      text-transform: capitalize;
      font-size: 2.4rem;
      color: ${({ theme }) => theme.colors.text};
      margin: 3rem 0;
      font-weight: 550;
    }
    .contact-form{
      width: 100%;
      height:100%;
      text-align:center;
      display: flex;
      flex-direction:column;
      align-items:center;
      justify-content:center;
      margin:6rem 0 2rem 0;

      .contact-inputs{display: flex;
      gap:2rem;
      flex-direction:column;
      align-items:center;
      justify-content:center;}

      input ,
      textarea{
        width:40rem;
      }
      Button{
        padding:.8rem 1.3rem;
      }
    }
  }
  @media (max-width: ${({ theme }) => theme.media.mobile}) {
    .contact_form_portion{
      width: 100%;
    height:100%;
    .contact-form{
      width: 100%;
      height:100%;
      text-align:center;
      display: flex;
      flex-direction:column;
      align-items:center;
      justify-content:center;
      margin:6rem 0 2rem 0;

      .contact-inputs{display: flex;
      gap:2rem;
      flex-direction:column;
      align-items:center;
      justify-content:center;}

      input ,
      textarea{
        max-width:28rem;
      }
      Button{
        padding:.8rem 1.3rem;
      }
    }
    }
  }
`;
export default Contact;
