import { createGlobalStyle } from "styled-components"
import Main from "./Main";
export const GlobalStyle  = createGlobalStyle`
body{
    height: 100%;
    width:100%;
    alignItems: center;
    background: {({theme}) => theme.body.bg };
}
`;
