import React from "react";
import styled from "styled-components";
import Count from "./Count";
import FormatPrice from "./FormatPrice";
import DeleteIcon from "@mui/icons-material/Delete";
import { useCartContext } from "../context/cart_context";

const CartItem = ({ id, name, image, price, count, color, stock }) => {
  const { deleteItem, setIncrement, setDecrement } = useCartContext();

  return (
    <Wrapper>
      <div className="cart_data">
        <div className="name_image_color">
          <img className="cart_image" src={image} alt={name} />
          <div className="grid_two_column">
            <p className="name">{name}</p>
            <div className="color_div">
              <p>Color:</p>
              <div
                className="color_style"
                style={{ background: color, color: color }}
              ></div>
            </div>
          </div>
        </div>
        <div className="hide-cart">
          <p>
            <FormatPrice price={price} />
          </p>
        </div>

        <div className="count_btn">
          <Count
            count={count}
            setDecrement={() => setDecrement(id)}
            setIncrement={() => setIncrement(id)}
          />
        </div>

        <div className="hide-cart">
          <p>
            <FormatPrice price={price * count} />
          </p>
        </div>
        <div className="delete_item">
          <DeleteIcon className="delete_icon" onClick={() => deleteItem(id)} />
        </div>
      </div>
    </Wrapper>
  );
};
const Wrapper = styled.section`
  max-width: 90rem 0;
  .container {
    margin: 10rem 20rem 10rem 20rem;
  }
  p {
    font-size: 1.6rem;
  }
  .cart_data {
    margin: 2rem 0;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: row;
    gap: 12rem;

    .name_image_color {
      display: flex;
      justify-content: center;
      align-items: center;
      gap: 1rem;

      .grid_two_column {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        gap: 0.3rem;

        .color_div {
          display: flex;
          gap: 0.5rem;
        }
      }
      .name {
        display: flex;
        flex-direction: column;
        font-size: 1.5rem;
      }
      img {
        width: 5.8rem;
        height: 4.7rem;
      }
    }

    .color_style {
      border: none;
      border-radius: 50%;
      width: 2rem;
      height: 2rem;
    }
    .delete_item {
      .delete_icon {
        font-size: 2.4rem;
      }
    }
  }

  @media (max-width: ${({ theme }) => theme.media.small_phone}) {
    .hide-cart {
      display: none;
    }
  }
`;
export default CartItem;
