import React from "react";
import styled from "styled-components";
import { useCartContext } from "./context/cart_context";
import CartItem from "../components/components/CartItem";
import { Button } from "../components/components/Button";
import { NavLink } from "react-router-dom";
import FormatPrice from "./components/FormatPrice";

const Cart = () => {
  const { cart, clearCart, total_amount, shipping_fee } = useCartContext();
  console.log(cart);

  //Empty page of a cart
  if (cart.length === 0) {
    return (
      <EmptyDiv>
        <h3>No items in cart</h3>
      </EmptyDiv>
    );
  }

  return (
    <Wrapper>
      <div>
      <div className="container">
        <div className="cart_heading grid-five-column">
          <p>Item</p>
          <p className="cart_hide">Price</p>
          <p>Quantity</p>
          <p>SubTotal</p>
          <p>Remove</p>
        </div>
        <hr />
        <div className="cart_item grid-five-row">
          {cart.map((currentData) => {
            return <CartItem key={currentData.id} {...currentData} />;
          })}
        </div>
        <hr />
        <div className="cart_btn">
          <NavLink to="/products">
            <Button>CONTINUE SHOPPING </Button>
          </NavLink>
          <Button className="btn clear_cart_btn" onClick={clearCart}>
            CLEAR CART
          </Button>
        </div>

        <div className="total_part">
          <div className="grid_two_column">
            <div  className="grid_two_row">
              <p>SUBTOTAL:</p>
              <p>
                <FormatPrice price={total_amount} />
              </p>
              </div>
              <div className="grid_two_row">
                <p>SHIPPING FEE:</p>
                <p>
                  <FormatPrice price={shipping_fee} />
                </p>
              </div>
              <div className="grid_two_row">
                <p>TOTAL:</p>
                <p>
                  <FormatPrice price={shipping_fee + total_amount} />
                </p>
              </div>
          </div>
        </div>
      </div>

      </div>    
      </Wrapper>
  );
};
const EmptyDiv = styled.div`
  h3 {
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 16rem 0;
    font-size: 2rem;
  }
`;

const Wrapper = styled.section`
  .container {
    max-width: 90rem 0;
    margin: 10rem 20rem 20rem 20rem;

    .grid-five-column {
      gap:2rem;
      display:grid;
      grid-template-columns: repeat(5, 20rem);
    }

    .grid-five-row{
      gap:2rem;
      display:grid;
      grid-template-rows: repeat(5, 20rem);
    }

    .cart_btn {
      display: flex;
      justify-content: space-between;
      align-items: center;

      .clear_cart_btn {
        background: #fa8072;
      }
    }
    .total_part {
      width: auto;
      height: auto;
      background: ${({ theme }) => theme.colors.bg};
      float:right;
      padding: 2rem;
      margin-top:2rem;

      .grid_two_column{
         grid-template-columns: repeat(2, 10rem);

        .grid_two_row{
           display:flex;
           align-items:center;
           justify-content:space-between;
           flex-direction:row;
           gap:2rem
        }
        p{
          font-size:1.5rem;
          font-weight:544;
        }
      }
    }
  }
`;
export default Cart;
