.container {
  max-width: 120rem;
  margin: 0 auto;
}

.grid {
  display: grid;
  gap: 9rem;
  margin:8rem 16rem 0rem 16rem;
}

.grid_two_column {
  grid-template-columns: repeat(2, 1fr);

}
