import { createContext, useContext, useEffect, useReducer } from "react";
import reducer from "../reducer/cartReducer";

const CartContext = createContext();

const getLocalCartData = () => {
  let localCartData = localStorage.getItem("cartItems");
  if (localCartData === []) {
    return [];
  }else{
    return JSON.parse(localCartData);
  }
};
const initialState = {
  // cart: [],
  cart: getLocalCartData(),
  total_items: "",
  total_amount: 100,
  shipping_fee: 5000,
};

const CartContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const addTocart = (id, color, count, product) => {
    dispatch({ type: "ADD_TO_CART", payload: { id, color, count, product } });
  };
  const deleteItem = (id) => {
    dispatch({ type: "DELETE_ITEM", payload: id });
  };

// increment and decrement 

const setDecrement = (id) => {
dispatch({ type:"SET_DECREMENT" , payload: id })
}

const setIncrement = (id) => {
  dispatch({ type:"SET_INCREMENT" , payload: id })
  }

  const clearCart = () => {
    dispatch({type:"CLEAR_CART"});
  }

  // local  storage//
  useEffect(() => {
    dispatch({type:"CART_SUBTOTAL"});
    dispatch({type:"CART_TOTAL_ITEM"});
    localStorage.setItem("cartItems", JSON.stringify(state.cart));
  }, [state.cart]);

  return (
    <CartContext.Provider value={{ ...state, addTocart, deleteItem ,clearCart ,setIncrement ,setDecrement}}>
      {children}
    </CartContext.Provider>
  );
};

const useCartContext = () => {
  return useContext(CartContext);
};

export { CartContextProvider, useCartContext };
