              expectations. Here is what some of them have to say about us.
            </p>
          </div>
          {/* <div className="grid grid-three-column card_detail"> */}
          <OwlCarousel
          className="owl-theme"
          items="3"
          autoplay="true"
          nav
          dots="false"
          loop
          margin="1rem"
          >

            {state.map((currentData, index) => {
              const { description, title } = currentData;
              return (
                <>
                  <div key={index}>
                    <Card description={description} title={title} />
                  </div>
                </>
              );
            })}
          </OwlCarousel>
          </div>
        {/* </div> */}
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
