import React from "react";
import styled from "styled-components";
import Count from "./Count";
const CartItem = ({ id, name, image, price, count, color }) => {
  return (
    <Wrapper>
      <div className="cart_heading grid-five-column">
        <div className="cart_image_name grid_two_column ">
          <img src={image} alt={name} />
          <div>
          <p>{name}</p>
          <p>Color:</p>
          </div>
         <div>
         <p>{price}</p>
         </div>
         <div>
         <Count/>
         </div>
        </div>
      </div>
    </Wrapper>
  );
};
const Wrapper = styled.section`
.cart_image_name{
display:flex;
justify-content:space-around;
align-items:center;
flex-direction:row;
gap:1.3rem;

  img{
    width:5.8rem;
    height:4.7rem;
  }
  p{
    font-size:1.3rem;
  }
}
`;
export default CartItem;
