import React from 'react'
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import FormatPrice from "./FormatPrice";

const ListView = ({products}) => {
    const { id, name, image, price, category } = currenElement;
  return (
    <>
        <Wrapper>
        <NavLink to={`/singleproduct/${id}`}>
        {
            products.map((currentData) => {
                const { id, name, image, price, category } = currentData;
                return(
                    <>
                    <div className="card">
          <figure>
            <img className="product_img" src={image} alt={name} />
            <figcaption className="product_caption">{category}</figcaption>
          </figure>
          <div className="card_data">
            <div className="class_data_flex">
              <h3>{name}</h3>
              <p className="price_format"><FormatPrice price={price}/></p>
            </div>
          </div>
        </div>
                    </>
                )
            })
        }

      </NavLink>
      </Wrapper>
    </>
  )
}
const Wrapper = styled.section`
  .grid {
    display: grid;
    gap: 9rem;
    margin: 2rem 2rem 0rem 2rem;
  }
`;
export default ListView;
