import { Button } from "./Button";
import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";

const Main_Section = ({ myData, para, image, btn }) => {
  const { nameData } = myData;
  const { paraData } = para;
  const { imageData } = image;
  const { btnData } = btn;
  return (
    <>
      <Wrapper>
        <div className="container main-section">
          <div className="grid grid_two_column">
            <div className="main_section">
              <h1 className="heading animX">{nameData}</h1>
              <p className="main_para anim">{paraData}</p>
              <NavLink>
                <Button className="anim">{btnData}</Button>
              </NavLink>
            </div>
            <div classname="main_section_image">
              <figure>
                <img
                  src={imageData}
                  alt="main_section_image"
                  className="image"
                />
              </figure>
            </div>
          </div>
        </div>
      </Wrapper>
    </>
  );
};
const Wrapper = styled.section`
  padding: 9rem 0;

  img {
    min-width: 20rem;
    height: 20rem;
  }
  .grid {
   display: grid;
   gap: 9rem;
   margin:8rem 0rem 0rem 0rem;
  }
  .main-section{
    background: {({theme}) theme.colors.};
  .main_section_image {
    width: 100%;
    height: auto;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .image {
    width: 80%;
    height: auto;
  }
  .heading.anim {
    animation-delay: 0.5s;
  }
  .main_para.anim {
    animation-delay: 1s;
  }
  Button.anim {
    animation-delay: 1.5s;
  }
  .animX {
    opacity: 0;
    transform: translateX(-80px);
    animation: moveup 0.5s linear forwards;
  }
  .anim {
    opacity: 0;
    transform: translateY(30px);
    animation: moveup 0.5s linear forwards;
  }
  @keyframes moveup {
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }
}
  @media (max-width: ${({ theme }) => theme.media.tab}) {
    .grid {
      display: grid;
      align-items: center;
      justify-content: center;
      margin: 0rem 16rem 0rem 16rem;
    }
    .grid_two_column {
      grid-template-columns: repeat(2, 1fr);
    }
    img {
      min-width: 24rem;
      height: 22rem;
    }

  }
  @media (max-width: ${({ theme }) => theme.media.mobile}) {
    html {
      font-size: 60%;
    }
    .grid {
      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      margin: 0rem 16rem 0rem 16rem;
    }
    .grid_two_column {
      grid-template-columns: (1, 2fr);
    }
    img {
      min-width: 24rem;
      height: 22rem;
    }
  }
    @media (max-width: ${({ theme }) => theme.media.small_phone}) {

    }

`; // 1fr=25% of the available space.
export default Main_Section;
