import styled from "styled-components";

export const Button = styled.button`
text-decoration:none;
list-style: none;
color:#061849;
text-transform: uppercase;
font-size: 1.4rem;
font-weight: 500;
border:none;
padding: 0.6rem;
cursor: pointer;
background: ${({theme}) =>theme.colors.helper};
/* margin-top:rem; */
margin:auto;
`;
